import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function ConversionSelector() {
  const [conversionMethod, setConversionMethod] = useState('client');

  const handleToggleChange = (e) => {
    setConversionMethod(e.target.value);
  };

  return (
    <div>
      <h1>Select Conversion Method</h1>
      <div>
        <label>
          <input
            type="radio"
            name="conversionMethod"
            value="client"
            checked={conversionMethod === 'client'}
            onChange={handleToggleChange}
          />
          Convert using Client
        </label>
        <label>
          <input
            type="radio"
            name="conversionMethod"
            value="server"
            checked={conversionMethod === 'server'}
            onChange={handleToggleChange}
          />
          Convert using Server
        </label>
      </div>
      {/* Use Link for navigation */}
      <Link to={`/${conversionMethod}`}>
        <button>Start Conversion</button>
      </Link>
    </div>
  );
}

export default ConversionSelector;
