import React, { useState, useEffect, useRef } from 'react';
import io from 'socket.io-client';
import './MarkDown.css';
import { Prism as SyntaxHighlighter } from 'react-syntax-highlighter';
import { solarizedlight } from 'react-syntax-highlighter/dist/esm/styles/prism';
import { highlight, languages ,markdown} from 'prismjs/components/prism-core';
import 'prismjs/components/prism-markdown';

import { Editor } from 'react-simple-code-editor';

const socket = io('http://localhost:3001'); 

function MarkDownWithServer() {
  const [markdownInput, setMarkdownInput] = useState('');
  const [htmlOutput, setHtmlOutput] = useState('');
  const outputContainerRef = useRef(null);

  useEffect(() => {
    socket.on('html', (html) => {
      setHtmlOutput(html);
      outputContainerRef.current.scrollTop = outputContainerRef.current.scrollHeight;
    });

    return () => {
      socket.off('html');
    };
  }, []);

  const handleMarkdownChange = (newMarkdown) => {
    setMarkdownInput(newMarkdown);
    socket.emit('markdown', newMarkdown);
  };

  return (
    <div>
      <h1 style={{ textAlign: 'center' }}>Markdown to HTML Converter</h1>
      <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'flex-start' }}>
        <div style={{ width: '45%', marginRight: '10px' }}>
          <h2 style={{ textAlign: 'center' }}>Markdown Input</h2>
          <Editor
            value={markdownInput}
            onValueChange={handleMarkdownChange}
            highlight={(code) => highlight(code, languages.markdown)}
            padding={10}
            style={{
              fontFamily: '"Fira code", "Fira Mono", monospace',
              fontSize: 14,
              border: '1px solid #ccc',
              height: '350px',
              width: '100%',
            }}
          />
        </div>
        <div style={{ width: '45%' }}>
          <h2 style={{ textAlign: 'center' }}>HTML Output</h2>
          <div ref={outputContainerRef} style={{ overflow: 'auto', padding: '10px', border: '1px solid #ccc', height: '338px' }}>
            <SyntaxHighlighter language="html" style={solarizedlight}>
              {htmlOutput}
            </SyntaxHighlighter>
          </div>
        </div>
      </div>
    </div>
  );
}

export default MarkDownWithServer;
