import React, { useState, useEffect, useRef } from 'react';
import io from 'socket.io-client';
import './MarkDown.css';
import { Prism as SyntaxHighlighter } from 'react-syntax-highlighter';
import { solarizedlight } from 'react-syntax-highlighter/dist/esm/styles/prism';

const socket = io('http://localhost:3001'); // Replace 'http://localhost:3001' with your server URL

function MarkDownWithServer() {
  const [markdownInput, setMarkdownInput] = useState('');
  const [htmlOutput, setHtmlOutput] = useState('');
  const outputContainerRef = useRef(null);

  useEffect(() => {
    socket.on('html', (html) => {
      setHtmlOutput(html);
      // Scroll the output container to the bottom
      outputContainerRef.current.scrollTop = outputContainerRef.current.scrollHeight;
    });

    return () => {
      socket.off('html');
    };
  }, []);

  const handleMarkdownChange = (e) => {
    const markdown = e.target.value;
    setMarkdownInput(markdown);
    socket.emit('markdown', markdown);
  };

  return (
    <div>
      <h1>Markdown to HTML Converter</h1>
      <div style={{ display: 'flex', justifyContent: 'space-between' }}>
        <div style={{ width: '49%' }}>
          <h2 style={{ textAlign: 'center' }}>Markdown Input</h2>
          <textarea
            id="markdown-input"
            style={{ width: '100%', height: '200px' }}
            value={markdownInput}
            onChange={handleMarkdownChange}
          ></textarea>
        </div>
        <div style={{ width: '49%', overflow: 'auto', padding: '10px', border: '1px solid #ccc', height: '200px' }} ref={outputContainerRef}>
          <h2 style={{ textAlign: 'center' }}>HTML Output</h2>
          <div>

          </div>
         
        </div>
      </div>
    </div>
  );
}

export default MarkDownWithServer;
