import React, { useState } from 'react';
import  { marked } from 'marked';
import { Prism as SyntaxHighlighter } from 'react-syntax-highlighter';
import { solarizedlight } from 'react-syntax-highlighter/dist/esm/styles/prism';

function MarkDown() {
  const [markdownInput, setMarkdownInput] = useState('');

  const handleMarkdownChange = (e) => {
    const markdown = e.target.value;
    setMarkdownInput(markdown);
  };

  const htmlOutput = marked(markdownInput);

  return (
    <div>
      <h1>Markdown to HTML Converter with Syntax Highlighting</h1>
      <div style={{ display: 'flex' }}>
        <div style={{ width: '50%', paddingRight: '10px' }}>
          <textarea
            id="markdown-input"
            style={{ width: '100%', height: '200px' }}
            value={markdownInput}
            onChange={handleMarkdownChange}
          ></textarea>
        </div>
        <div style={{ width: '50%', overflow: 'auto', padding: '10px', border: '1px solid #ccc' }}>
          <SyntaxHighlighter language="markdown" style={solarizedlight}>
            {markdownInput}
          </SyntaxHighlighter>
        </div>
      </div>
      <div style={{ display: 'flex', marginTop: '20px' }}>
        <div style={{ width: '50%', overflow: 'auto', padding: '10px', border: '1px solid #ccc' }}>
          <h2>HTML Output</h2>
          <SyntaxHighlighter language="html" style={solarizedlight}>
            {htmlOutput}
          </SyntaxHighlighter>
        </div>
      </div>
    </div>
  );
}

export default MarkDown;
